<template>
  <view class="inline-block" v-show="show">
    <view
      class="c-loading-circle"
      :style="[cricleStyle]"
      v-if="mode == 'circle'"
    ></view>
    <view
      class="c-loading-flower"
      :style="[flowerStyle]"
      v-if="mode == 'flower'"
    >
    </view>
  </view>
</template>

<script>
import { calcColor } from '../../libs/helper/utils.js'

export default {
  name: 'c-loading',
  props: {
    // 动画的类型
    mode: {
      type: String,
      default: 'circle'
    },
    // 动画的颜色
    color: {
      type: String,
      default: uni.$c.color.disable
    },
    // 加载图标的大小，单位rpx
    size: {
      type: [String, Number],
      default: '17'
    },
    // 是否显示动画
    show: {
      type: Boolean,
      default: true
    }
  },
  computed: {
    // 加载中圆圈动画的样式
    cricleStyle() {
      let style = {}
      const size = this.$c.addUnit(this.size)

      style.width = size
      style.height = size
      const borderColor = this.$c.color.border

      style.borderColor = `${borderColor} ${borderColor} ${borderColor} ${calcColor(
        this.color
      )}`
      return style
    },
    flowerStyle() {
      let style = {}
      const size = this.$c.addUnit(this.size)
      style.width = size
      style.height = size
      return style
    }
  }
}
</script>

<style lang="scss" scoped>
.c-loading-circle {
  display: inline-flex;
  vertical-align: middle;
  width: 17px;
  height: 17px;
  background: 0 0;
  border-radius: 50%;
  border: 2px solid;
  border-color: $c-border-color $c-border-color $c-border-color
    $c-disabled-color;
  animation: c-circle 1s linear infinite;
}
@-webkit-keyframes c-circle {
  0% {
    transform: rotate(0);
  }

  100% {
    transform: rotate(360deg);
  }
}

.c-loading-flower {
  width: 20px;
  height: 20px;
  display: inline-block;
  vertical-align: middle;
  -webkit-animation: a 1s steps(12) infinite;
  animation: c-flower 1s steps(12) infinite;
  background: transparent
    url(data:image/svg+xml;base64,)
    no-repeat;
  background-size: 100%;
}
@keyframes c-flower {
  0% {
    -webkit-transform: rotate(0deg);
    transform: rotate(0deg);
  }

  to {
    -webkit-transform: rotate(1turn);
    transform: rotate(1turn);
  }
}
</style>
